<script>
    import {pinyin} from 'pinyin-pro';

    let zhValue;
    let pinyinValue = '';

    function onEnter(e) {
        if (e.charCode === 13) {
            pinyinValue = pinyin(zhValue)
        }
    }
</script>

<main>
    <h1>输入汉字获取拼音</h1>
    <input type="text" bind:value={zhValue} on:keypress={onEnter}/>
    <p>{pinyinValue}</p>

    <img src="data:image/png;base64," style="max-width: 100%;height: auto;" alt/>
    <p>Copyright © <a href="https://afoo.me">王福强</a>个人版权所有 - Since 2021</p>
</main>

<style>
    main {
        text-align: center;
        padding: 1em;
        max-width: 240px;
        margin: 0 auto;
    }

    h1 {
        color: #ff3e00;
        text-transform: uppercase;
        font-size: 4em;
        font-weight: 100;
    }

    @media (min-width: 640px) {
        main {
            max-width: none;
        }
    }
</style>